void nametables(void)
{
    gXNametableLoad = 0x00;
    gXNametableScreen = 0x00;
    ppu_row = 0;
    
    
    //for( j = 0; j != 0xFF; j++)
    //{
    //    update_nametables();
    //}
    //update_nametables();
    //for( j = 0; j != 0xFF; j++)
    //{
    //    update_nametables();
    //}
    //update_nametables();

    PPU_ADDRESS = 0x20;
    PPU_ADDRESS = 0x0;
    for( i = 0; i < 0x100; i++ )
    {
        PPU_DATA    = nametable[i];
    }
    PPU_ADDRESS = 0x21;
    PPU_ADDRESS = 0;
    for( i = 0; i < 0x100; i++ )
    {
        PPU_DATA    = nametable[i+0x100];
    }
    PPU_ADDRESS = 0x22;
    PPU_ADDRESS = 0;
    for( i = 0; i < 0x100; i++ )
    {
        PPU_DATA    = nametable[i+0x200];
    }
    PPU_ADDRESS = 0x23;
    PPU_ADDRESS = 0;
    for( i = 0; i < 0xC0; i++ )
    {
        PPU_DATA    = nametable[i+0x300];
    }
    
    // Attributes
    for( i = 0; i < 0x40; i++ )
    {
        PPU_DATA = 0x55;
    }
    
    PPU_ADDRESS = 0x28;
    PPU_ADDRESS = 0x0;
    for( i = 0; i < 0x100; i++ )
    {
        PPU_DATA    = nametable[0x3C0+i];
    }
    PPU_ADDRESS = 0x29;
    PPU_ADDRESS = 0;
    for( i = 0; i < 0x100; i++ )
    {
        PPU_DATA    = nametable[0x4C0+i];
    }
    PPU_ADDRESS = 0x2A;
    PPU_ADDRESS = 0;
    for( i = 0; i < 0x100; i++ )
    {
        PPU_DATA    = nametable[0x5C0+i];
    }
    PPU_ADDRESS = 0x2B;
    PPU_ADDRESS = 0;
    for( i = 0; i < 0xC0; i++ )
    {
        PPU_DATA    = nametable[0x6C0+i];
    }

    // Attributes
    for( i = 0; i < 0x40; i++ )
    {
        PPU_DATA = 0x55;
    }

    gXNametableLoad = 0x40;
}

void sprites(void)
{    
    unsigned int i;
    
    // clear sprite ram
    for( i = 0; i < 256; i++ )
    {
        *((unsigned char*)(0x200 + i)) = 0x00;
    }        
        
    // From NESDev:
    //+------+----------+--------------------------------------+
    //+ Byte | Bits     | Description                          |
    //+------+----------+--------------------------------------+
    //|  0   | YYYYYYYY | Y Coordinate - 1. Consider the coor- |
    //|      |          | dinate the upper-left corner of the  |
    //|      |          | sprite itself.                       |
    //|  1   | IIIIIIII | Tile Index #                         |
    //|  2   | vhp000cc | Attributes                           |
    //|      |          |   v = Vertical Flip   (1=Flip)       |
    //|      |          |   h = Horizontal Flip (1=Flip)       |
    //|      |          |   p = Background Priority            |
    //|      |          |         0 = In front                 |
    //|      |          |         1 = Behind                   |
    //|      |          |   c = Upper two (2) bits of colour   |
    //|  3   | XXXXXXXX | X Coordinate (upper-left corner)     |
    //+------+----------+--------------------------------------+}
    
    *((unsigned char*)0x200) = 0x50;
    *((unsigned char*)0x201) = 0x00;
    *((unsigned char*)0x202) = 0x00;
    *((unsigned char*)0x203) = 0x50;
    
    *((unsigned char*)0x204) = 0x50;
    *((unsigned char*)0x205) = 0x01;
    *((unsigned char*)0x206) = 0x00;
    *((unsigned char*)0x207) = 0x58;
    
    *((unsigned char*)0x208) = 0x58;
    *((unsigned char*)0x209) = 0x02;
    *((unsigned char*)0x20A) = 0x00;
    *((unsigned char*)0x20B) = 0x50;
    
    *((unsigned char*)0x20C) = 0x58;
    *((unsigned char*)0x20D) = 0x03;
    *((unsigned char*)0x20E) = 0x00;
    *((unsigned char*)0x20F) = 0x58;
    
    gX = 0x50;
    gY = 0x4F;
    gVelocity = 0;
    gVelocityDirection = 0;
    gXScroll = 0;
    gYScroll = 0;
    gYNametable = 2;
    
    *((unsigned char*)0x210) = 0x30;
    *((unsigned char*)0x211) = 0x04;
    *((unsigned char*)0x212) = 0x01;
    *((unsigned char*)0x213) = 0x50;
    
    *((unsigned char*)0x214) = 0x30;
    *((unsigned char*)0x215) = 0x05;
    *((unsigned char*)0x216) = 0x01;
    *((unsigned char*)0x217) = 0x58;
}

void dma_sprites(void)
{
    *((unsigned char*)0x4013) = 0x00;
    *((unsigned char*)0x4014) = 0x02;
}

void input_poll(void)
{
    // Strobe 0x4016 bit 0 to gather input
    *((unsigned char*)0x4016) |= 0x01;
    *((unsigned char*)0x4016) &= 0xFE;

    gController1 = 0x00;
    
     if(*((unsigned char*)0x4016) & 0x01)
     {
         gController1 |= C1_A;
     }
     if(*((unsigned char*)0x4016) & 0x01)
     {
         gController1 |= C1_B;
     }
     if(*((unsigned char*)0x4016) & 0x01)
     {
         gController1 |= C1_s;
     }
     if(*((unsigned char*)0x4016) & 0x01)
     {
         gController1 |= C1_S;
     }
     if(*((unsigned char*)0x4016) & 0x01)
     {
         gController1 |= C1_U;
     }
     if(*((unsigned char*)0x4016) & 0x01)
     {
         gController1 |= C1_D;
     }
     if(*((unsigned char*)0x4016) & 0x01)
     {
         gController1 |= C1_L;
     }
     if(*((unsigned char*)0x4016) & 0x01)
     {
         gController1 |= C1_R;
     }
         
}

