void do_physics(void)
{
    if( *((unsigned char*)0x210) != gY )
    {
        if( *((unsigned char*)0x210) < gY )
        {
            *((unsigned char*)0x210) += 1;
            *((unsigned char*)0x214) += 1;
        }
        else
        {
            *((unsigned char*)0x210) -= 1;
            *((unsigned char*)0x214) -= 1;
        }
    }
    if( *((unsigned char*)0x213) != gX )
    {
        if( *((unsigned char*)0x213) < gX )
        {
            *((unsigned char*)0x213) += 1;
            *((unsigned char*)0x217) += 1;
            
            *((unsigned char*)0x211) = 0x05;
            *((unsigned char*)0x212) = 0x41;
            *((unsigned char*)0x215) = 0x04;
            *((unsigned char*)0x216) = 0x41;
        }
        else
        {
            *((unsigned char*)0x213) -= 1;
            *((unsigned char*)0x217) -= 1;
            
            *((unsigned char*)0x211) = 0x04;
            *((unsigned char*)0x212) = 0x01;
            *((unsigned char*)0x215) = 0x05;
            *((unsigned char*)0x216) = 0x01;
        }
    }
        
            
    if( gVelocityDirection == 1 ) // moving up
    {
        for( i = 0; (i<<2) < gVelocity; i++ )
        {
            if( gYNametable == 2 )
            {
                if( nametable[0x3C0 + (((gY)&0xF8) << 2) + ((gX) >> 3)] == BACKGROUND_INDEX &&
                    nametable[0x3C0 + (((gY)&0xF8) << 2) + ((gX+0xF) >> 3)] == BACKGROUND_INDEX )
                {
                    if(gY > 0x0F)
                    {
                        gY -= 1;
                    }
                    else
                    {
                        gYScroll = 0xEF;
                        gYNametable = 0;
                    }
                }
                else
                {
                    gVelocity = 0;
                    gVelocityDirection = 0;
                    break;
                }
            }
            else if((gYScroll + gY) >= 0xF0 )
            {
                if( nametable[0x3C0 + (((gYScroll + gY - 0xF0)&0xF8) << 2) + ((gX) >> 3)] == BACKGROUND_INDEX &&
                    nametable[0x3C0 + (((gYScroll + gY - 0xF0)&0xF8) << 2) + ((gX+0xF) >> 3)] == BACKGROUND_INDEX )
                {
                    if(gY > 0x0F)
                    {
                        gY -= 1;
                    }
                    else
                    {
                        gYScroll -= 1;
                    }
                }
                else
                {
                    gVelocity = 0;
                    gVelocityDirection = 0;
                    break;
                }
            }
            else
            {
                if( nametable[(((gYScroll + gY - 0x100) & 0xF8) << 2) + ((gX) >> 3)] == BACKGROUND_INDEX &&
                    nametable[(((gYScroll + gY - 0x100) & 0xF8) << 2) + ((gX+0xF) >> 3)] == BACKGROUND_INDEX )
                {
                    if(gY > 0x0F)
                    {
                        gY -= 1;
                    }
                    else
                    {
                        if( gYScroll > 0 )
                        {
                            gYScroll -= 1;
                        }
                    }
                }
                else
                {
                    gVelocity = 0;
                    gVelocityDirection = 0;
                    break;
                }
            }
        }
        if( i == ((gVelocity+3)>>2) )
        {
            if( gVelocity == 0 )
            {
                gVelocityDirection = 0;
            }
            else
            {
                gVelocity -= 1;
            }
        }
    }
    else // moving down
    {
        for( i = 0; (i<<2) < gVelocity; i++ )
        {
            if(gYNametable == 2 )
            {
                if( nametable[0x3C0 + (((gY+0x11)&0xF8) << 2) + ((gX) >> 3)] == BACKGROUND_INDEX &&
                    nametable[0x3C0 + (((gY+0x11)&0xF8) << 2) + ((gX+0xF) >> 3)] == BACKGROUND_INDEX )
                {
                    if(gY < 0xCF)
                    {
                        gY += 1;
                    }
                    else
                    {
                        gVelocity = 0;
                        break;
                    }
                }
                else
                {
                    gVelocity = 0;
                    break;
                }
            }
            else if((gYScroll + gY + 0x11) >= 0xF0)
            {
                if( nametable[0x3C0 + (((gYScroll + gY+0x11 - 0xF0)&0xF8) << 2) + ((gX) >> 3)] == BACKGROUND_INDEX &&
                    nametable[0x3C0 + (((gYScroll + gY+0x11 - 0xF0)&0xF8) << 2) + ((gX+0xF) >> 3)] == BACKGROUND_INDEX )
                {
                    if(gY < 0xCF)
                    {
                        gY += 1;
                    }
                    else
                    {
                        if( gYScroll == 0xEF )
                        {
                            gYNametable = 2;
                            gYScroll = 0;
                            gVelocity = 0;
                            break;
                        }
                        else
                        {
                            gYScroll+=1;
                        }
                    }
                }
                else
                {
                    gVelocity = 0;
                    break;
                }
            }
            else
            {
                if( nametable[(((gYScroll + gY+0x11)&0xF8) << 2) + ((gX) >> 3)] == BACKGROUND_INDEX &&
                    nametable[(((gYScroll + gY+0x11)&0xF8) << 2) + ((gX+0xF) >> 3)] == BACKGROUND_INDEX )
                {
                    if(gY < 0xCF)
                    {
                        gY += 1;
                    }
                    else
                    {
                        gYScroll+=1;
                    }
                }
                else
                {
                    gVelocity = 0;
                    break;
                }
            }
        }
        
        if( i == ((gVelocity+3)>>2) )
        {
            gVelocity+=1;
        }
    }
}    
        
    

void scroll_right(void)
{
    // set bits [1:0] to 0 for nametable
    *((unsigned char*)0x2000) = 0x84 + gYNametable;
    // read 0x2002 to reset the address toggle
    devnull = *((unsigned char*)0x2002);
    // scroll a number of pixels in the X direction
    *((unsigned char*)0x2005) = gXScroll;
    // scroll a number of pixels in the Y direction
    *((unsigned char*)0x2005) = gYScroll;
}

